import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoginButton from './LoginButton';
import TopTracks from './TopTracks';
import TracksComponent from "./TracksComponent";

function App() {
    const [user1Data, setUser1Data] = useState({ accessToken: null, userId: null });
    const [user2Data, setUser2Data] = useState({ accessToken: null, userId: null });

    // Check if code is available and handle login
    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    if (!user1Data.accessToken) {
                        // Set user1 data if no accessToken is present for user1
                        setUser1Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    } else {
                        // Set user2 data if user1 is already logged in
                        setUser2Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    }
                })
                .catch(err => console.error('Error in token exchange:', err));
        }
    }, [user1Data.accessToken]);

    // Function to handle the login click based on user logins
    const handleLogin = () => {
        if (!user1Data.accessToken || !user2Data.accessToken) {
            window.location = 'http://localhost:3001/login';
        }
    };

    return (
        <div className="App">
            {/* Display LoginButton based on accessToken of users */}
            {!user1Data.accessToken || !user2Data.accessToken ? (
                <LoginButton handleLogin={handleLogin} />
            ) : null}

            {/* Display TopTracks if user1 is logged in */}
            {user1Data.accessToken && <TopTracks accessToken={user1Data.accessToken} userId={user1Data.userId} />}

            {/* Display TracksComponent if both users are logged in */}
            {user1Data.accessToken && user2Data.accessToken && <TracksComponent />}
        </div>
    );
}

export default App;
