    const instructions = length === 1 ? 'Please change the user when logging in.' : '';

    return (
        <Button
            variant="outline-primary"
            className={`login-button ${buttonClass}`}
            onClick={() => window.location = 'http://localhost:3001/login'}
        >
            {buttonText}
