import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoginButton from './LoginButton';
import TopTracks from './TopTracks';
import TracksComponent from "./TracksComponent";

function App() {
    const [user1Data, setUser1Data] = useState({ accessToken: null, userId: null });
    const [user2Data, setUser2Data] = useState({ accessToken: null, userId: null });
    const [userCount, setUserCount] = useState(0);  // Tracks the number of users logged in

    useEffect(() => {
        // Fetch the current user count from backend
        axios.get('http://localhost:3001/user-ids')
            .then(res => {
                setUserCount(res.data.length);  // Update user count based on backend data
                // Additional logic to handle accessToken and userId could be placed here
            })
            .catch(err => console.error('Error fetching user data:', err));

        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    if (userCount === 0) {
                        setUser1Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    } else if (userCount === 1) {
                        setUser2Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    }
                })
                .catch(err => console.error('Error in token exchange:', err));
        }
    }, [userCount]);

    return (
        <div className="App">
            {userCount === 0 ? <LoginButton /> : null}
            {userCount === 1 ? <LoginButton /> : null}
            {userCount >= 2 ? <TracksComponent /> : null}
        </div>
    );
}

export default App;
