import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, Box, Button, CircularProgress } from '@mui/material';
import ComparisonPage from './ComparisonPage'; // Assuming this is your results display component

function App() {
    const [userAccessToken, setUserAccessToken] = useState(null);
    const [secondUserAccessToken, setSecondUserAccessToken] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            setLoading(true);
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    if (!userAccessToken) {
                        setUserAccessToken(res.data.accessToken);
                    } else {
                        setSecondUserAccessToken(res.data.accessToken);
                    }
                    setLoading(false);
                })
                .catch(err => {
                    console.error('Error in token exchange:', err);
                    setLoading(false);
                });
        }
    }, [userAccessToken]);

    const handleLogin = () => {
        // Assuming your login URL would properly reset to get a new user each time
        window.location = `http://localhost:3001/login`;
    };

    if (userAccessToken && secondUserAccessToken) {
        return (
            <ComparisonPage
                userOneAccessToken={userAccessToken}
                userTwoAccessToken={secondUserAccessToken}
            />
        );
    }

    return (
        <Container maxWidth="sm">
            <Box mt={10} textAlign="center">
                <Typography variant="h3" gutterBottom>Welcome to Spotify Stats</Typography>
                <Typography variant="subtitle1" gutterBottom>
                    Log in to compare your top tracks on Spotify with another user.
                </Typography>
                {loading ? (
                    <CircularProgress color="inherit" />
                ) : (
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleLogin}
                    >
                        {userAccessToken ? "Login with the Second User" : "Login with the First User"}
                    </Button>
                )}
            </Box>
        </Container>
    );
}

export default App;
