import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, Box, Button, CircularProgress } from '@mui/material';
import ComparisonPage from './ComparisonPage'; // Your comparison results component

function App() {

    const [userAccessToken, setUserAccessToken] = useState(localStorage.getItem('userAccessToken'));
    const [secondUserAccessToken, setSecondUserAccessToken] = useState(localStorage.getItem('secondUserAccessToken'));
    const [loading, setLoaing] = useState(false);

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code && !userAccessToken) {
            setLoading(true);
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    localStorage.setItem('userAccessToken', res.data.accessToken);
                    setUserAccessToken(res.data.accessToken);
                    window.location.href = '/'; // Clear URL parameters
                    setLoading(false);
                })
                .catch(err => {
                    console.error('Error in token exchange:', err);
                    setLoading(false);
                });
        } else if (code && userAccessToken) {
            setLoading(true);
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    localStorage.setItem('secondUserAccessToken', res.data.accessToken);
                    setSecondUserAccessToken(res.data.accessToken);
                    window.location.href = '/'; // Clear URL parameters
                    setLoading(false);
                })
                .catch(err => {
                    console.error('Error in token exchange:', err);
                    setLoading(false);
                });
        }
    }, []);

    const handleLogin = () => {
        // Triggering the login process
        window.location = `http://localhost:3001/login`;
    };

    if (userAccessToken && secondUserAccessToken) {
        return <ComparisonPage userOneAccessToken={userAccessToken} userTwoAccessToken={secondUserAccessToken} />;
    }

    return (
        <Container maxWidth="sm">
            <Box mt={10} textAlign="center">
                <Typography variant="h3" gutterBottom>Welcome to Spotify Stats</Typography>
                <Typography variant="subtitle1" gutterBottom>
                    Log in to compare your top tracks on Spotify with another user.
                </Typography>
                {loading ? (
                    <CircularProgress color="inherit" />
                ) : (
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleLogin}
                    >
                        {userAccessToken ? "Login with the Second User" : "Login with the First User"}
                    </Button>
                )}
            </Box>
        </Container>
    );
}

export default App;
