import React from 'react';
import { Button } from 'react-bootstrap'; // Assuming you're using react-bootstrap

const LoginButton = ({ length }) => {
    const buttonText = length === 0 ? 'Log in with Spotify for the first user' : 'Continue login for the next user';
    const instructionText = length === 1 ? 'Please change the user when logging in.' : '';

    return (
        <div>
            <Button
                variant="primary"
                onClick={() => window.location = 'http://localhost:3001/login'}
                className="mt-4 font-weight-bold"
            >
                {buttonText}
            </Button>
            {length === 1 && <p className="mt-2 text-muted">{instructionText}</p>}
        </div>
    );
};
