import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoginButton from './LoginButton';
import TopTracks from './TopTracks';
import TracksComponent from "./TracksComponent";

function App() {
    const [users, setUsers] = useState([]);

    useEffect(() => {
        async function fetchUserData(code) {
            try {
                const response = await axios.post('http://localhost:3001/callback', { code });
                setUsers(prev => [...prev, { accessToken: response.data.accessToken, userId: response.data.userId }]);
            } catch (error) {
                console.error('Error in token exchange:', error);
            }
        }

        const code = new URLSearchParams(window.location.search).get('code');
        if (code && users.length < 2) { // Ensure we only fetch data for two users
            fetchUserData(code);
        }
    }, [users.length]); // Dependency on users.length to limit useEffect activation

    return (
        <div className="App">
            {/* Conditional rendering based on users' login status */}
            {users.length === 0 && <LoginButton />}
            {users.length === 1 && <LoginButton />}
            {users.map((user, index) => (
                <TopTracks key={index} accessToken={user.accessToken} userId={user.userId} />
            ))}
            {/* Render TracksComponent only when both users are logged in */}
            {users.length === 2 && <TracksComponent />}
        </div>
    );
}

export default App;
