import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TopTracks = ({ accessToken, userId }) => {
    const [tracks, setTracks] = useState([]);

    useEffect(() => {
        const fetchTopTracks = async () => {
            try {
                // Fetch top tracks from Spotify
                const { data } = await axios.get('https://api.spotify.com/v1/me/top/tracks', {
                    headers: {
                        Authorization: `Bearer ${accessToken}`,
                    },
                });

                // Simplify the data structure to include only artist and song name
                const simplifiedTracks = data.items.map(track => ({
                    artist: track.artists.map(artist => artist.name).join(", "), // Assuming there may be multiple artists
                    song: track.name
                }));

                // Store the simplified data in Redis
                await storeTracksInRedis(simplifiedTracks);

                // Update state with simplified track data
                setTracks(simplifiedTracks);
            } catch (err) {
                console.error('Error fetching top tracks:', err);
            }
        };

        const storeTracksInRedis = async (tracks) => {
            try {
                await axios.post('http://localhost:3001/store-tracks', {
                    tracks,
                    userId
                });
            } catch (err) {
                console.error('Error sending tracks to Redis:', err);
            }
        };

        if (accessToken && userId) {
            fetchTopTracks();
        }
    }, [accessToken, userId]); // Dependency array includes userId

    return (
        <div>
            <h2>Top Played Songs</h2>
            <ul>
                {tracks.map((track, index) => (
                    <li key={index}>
                        {track.name} by {track.artists.map(artist => artist.name).join(", ")}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TopTracks;
