import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LoginButton from './LoginButton';
import TopTracks from './TopTracks';

function Appa() {
    const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken'));
    const [userId, setUserId] = useState(localStorage.getItem('userId'));

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            authenticateUser(code);
        }
    }, []);

    const authenticateUser = (code) => {
        axios.post('http://localhost:3001/callback', { code })
            .then(res => {
                setAccessToken(res.data.accessToken);
                setUserId(res.data.userId);
                localStorage.setItem('accessToken', res.data.accessToken);
                localStorage.setItem('userId', res.data.userId);
            })
            .catch(err => {
                console.error('Error in token exchange:', err);
                clearSession(); // Clear session if there's an error
            });
    };

    const clearSession = () => {
        localStorage.removeItem('accessToken');
        localStorage.removeItem('userId');
        setAccessToken(null);
        setUserId(null);
        redirectToSpotifyLogin(); // Redirect to login immediately for second user
    };

    const redirectToSpotifyLogin = () => {
        window.location.href = 'http://localhost:3001/login'; // Redirect to your backend login route
    };

    return (
        <div className="App">
            {!accessToken ? (
                <LoginButton />
            ) : (
                <>
                    <TopTracks accessToken={accessToken} userId={userId} />
                    <button onClick={clearSession}>Log in Another User</button>
                </>
            )}
        </div>
    );
}


function App() {
    const [user1Data, setUser1Data] = useState({ accessToken: null, userId: null });
    const [user2Data, setUser2Data] = useState({ accessToken: null, userId: null });

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    if (!user1Data.accessToken) {
                        setUser1Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    } else {
                        setUser2Data({ accessToken: res.data.accessToken, userId: res.data.userId });
                    }
                })
                .catch(err => console.error('Error in token exchange:', err));
        }
    }, []);

    return (
        <div className="App">
            {!user1Data.accessToken ? <LoginButton /> : <TopTracks accessToken={user1Data.accessToken} userId={user1Data.userId} />}
            {user1Data.accessToken && !user2Data.accessToken ? <LoginButton /> : null}
            {user2Data.accessToken ? <TopTracks accessToken={user2Data.accessToken} userId={user2Data.userId} /> : null}
        </div>
    );
}

export default App;
