import React, { useEffect, useState } from 'react';
import axios from 'axios'; // Make sure you've installed axios

const Login = () => {
    // State to hold the authorization tokens
    const [tokens, setTokens] = useState(null);

    useEffect(() => {
        // Extract the code from URL parameters
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            exchangeCodeForToken(code);
        }
    }, []);

    const exchangeCodeForToken = (code) => {
        axios.post('http://localhost:3001/api/spotify/login', { code })
            .then(response => {
                setTokens(response.data); // Save the tokens in state
                // Optionally, redirect or change the component view here
            })
            .catch(error => console.error('Error exchanging code for token:', error));
    };

    // Render the component with a login button or show user's top track if tokens are available
    return (
        <div>
            {!tokens ? (
                <a href="YOUR_AUTH_URL">Login With Spotify</a>
            ) : (
                <TopTrack tokens={tokens} /> // Pass tokens to the TopTrack component
            )}
        </div>
    );
};

export default Login;
