import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, Box, Button, CircularProgress } from '@mui/material';
import TopTracks from './TopTracks';

function App() {
    const [userAccessToken, setUserAccessToken] = useState(null);
    const [secondUserAccessToken, setSecondUserAccessToken] = useState(null);
    const [loading, setLoading] = useState(false);
    const [step, setStep] = useState(1); // 1 for first user, 2 for second user

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            setLoading(true);
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    if (step === 1) {
                        setUserAccessToken(res.data.accessToken);
                        setStep(2); // Move to next step
                    } else {
                        setSecondUserAccessToken(res.data.accessToken);
                    }
                    setLoading(false);
                })
                .catch(err => {
                    console.error('Error in token exchange:', err);
                    setLoading(false);
                });
        }
    }, [step]);

    const handleLogin = () => {
        // Redirect to the login URL; in a real-world scenario, ensure the URL reflects the user step
        window.location = `http://localhost:3001/login?step=${step}`;
    };

    const content = () => {
        if (loading) {
            return <CircularProgress color="inherit" />;
        }
        if (!userAccessToken || (userAccessToken && !secondUserAccessToken && step === 2)) {
            return (
                <Button variant="contained" color="primary" onClick={handleLogin}>
                    {step === 1 ? "Login with the First User" : "Login with the Second User"}
                </Button>
            );
        }
        if (secondUserAccessToken) {
            return (
                <>
                    <TopTracks accessToken={userAccessToken} title="First User's Top Tracks" />
                    <TopTracks accessToken={secondUserAccessToken} title="Second User's Top Tracks" />
                </>
            );
        }
    };

    return (
        <Container maxWidth="sm">
            <Box mt={10} textAlign="center">
                <Typography variant="h3" gutterBottom>
                    Compare Your Music Tastes
                </Typography>
                <Typography variant="subtitle1" gutterBottom>
                    Log in to compare your top tracks on Spotify with another user. Start by logging in as the first user.
                </Typography>
                {content()}
            </Box>
        </Container>
    );
}

export default App;
