import React, {useEffect, useState} from 'react';
import axios from 'axios';

function TracksComponent() {
    const [userTracks, setUserTracks] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const userIds = ['769nk4wefuksb4d5slgcv9e94', 'anotherUserId']; // List of user IDs
        const fetchTracks = async (userId) => {
            try {
                const url = `http://localhost:3001/get-tracks/${userId}`;
                const response = await axios.get(url);
                return {
                    userId,
                    tracks: response.data.map(track => ({
                        ...track,
                        artist: track.artists.map(artist => artist.name).join(', ')
                    }))
                };
            } catch (err) {
                console.error('Error fetching tracks for user:', userId, err);
                throw err;
            }
        };

        setLoading(true);
        Promise.all(userIds.map(userId => fetchTracks(userId)))
            .then(results => {
                const newTracks = results.reduce((acc, current) => ({
                    ...acc,
                    [current.userId]: current.tracks
                }), {});
                setUserTracks(newTracks);
            })
            .catch(err => {
                setError('Failed to fetch tracks');
            })
            .finally(() => {
                setLoading(false);
            });
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h1>User Tracks</h1>
            {Object.keys(userTracks).map((userId) => (
                <div key={userId}>
                    <h2>Tracks for User ID: {userId}</h2>
                    <ul>
                        {userTracks[userId].length > 0 ? userTracks[userId].map((track, index) => (
                            <li key={index}>
                                {track.name} by {track.artist}
                            </li>
                        )) : <li>No tracks found.</li>}
                    </ul>
                </div>
            ))}
        </div>
    );
}

export default TracksComponent;
