import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Typography, Box, Button, CircularProgress } from '@mui/material';
import LoginButton from './LoginButton';
import TopTracks from './TopTracks';

function App() {
    const [accessToken, setAccessToken] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const code = new URLSearchParams(window.location.search).get('code');
        if (code) {
            setLoading(true);
            axios.post('http://localhost:3001/callback', { code })
                .then(res => {
                    setAccessToken(res.data.accessToken);
                    setLoading(false);
                })
                .catch(err => {
                    console.error('Error in token exchange:', err);
                    setLoading(false);
                });
        }
    }, []);

    const content = () => {
        if (loading) {
            return <CircularProgress color="inherit" />;
        }
        if (!accessToken) {
            return <LoginButton />;
        }
        return <TopTracks accessToken={accessToken} />;
    };

    return (
        <Container maxWidth="sm">
            <Box mt={10} textAlign="center">
                <Typography variant="h3" gutterBottom>
                    Welcome to Spotify Tastes Checker
                </Typography>
                <Typography variant="subtitle1" gutterBottom>
                    You can compare your tastes with another user 
                </Typography>
                {content()}
            </Box>
        </Container>
    );
}

export default App;
