import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TopTrack = ({ tokens }) => {
    const [topTrack, setTopTrack] = useState(null);

    useEffect(() => {
        if (tokens) {
            fetchTopTrack(tokens.accessToken);
        }
    }, [tokens]);

    const fetchTopTrack = (accessToken) => {
        axios.get(`http://localhost:3001/api/spotify/top-track?accessToken=${accessToken}`)
            .then(response => {
                setTopTrack(response.data); // Set the top track in state
            })
            .catch(error => console.error('Error fetching top track:', error));
    };

    if (!topTrack) {
        return <div>Loading your top track...</div>;
    }

    return (
        <div>
            <h1>Your Top Track</h1>
            <p>{topTrack.name} by {topTrack.artists[0].name}</p>
            <img src={topTrack.album.images[0].url} alt="Album Cover" style={{ width: '100%' }} />
        </div>
    );
};

export default TopTrack;
